import { useState } from "react";

const ResultBox = ({ movies }) => {
  const [isOpen1, setIsOpen1] = useState(true);
  return (
    <div className='box'>
      <button
        className='btn-toggle'
        onClick={() => setIsOpen1((open) => !open)}
      >
        {isOpen1 ? "–" : "+"}
      </button>
      {isOpen1 && (
        <ul className='list'>
          {movies?.map((movie) => (
            <List movie={movie} />
          ))}
        </ul>
      )}
    </div>
  );
};

export default ResultBox;
