import { useEffect, useState } from "react";
import NavBar from "./NavBar";
import SearchBox from "./SerachBox";
import ResultsNumber from "./ResultsNumber";
import Box from "./Ui/Box";
import Loader from "./Ui/Loader";
import ErrorMessage from "./Ui/ErrorMessage";
import ResultsList from "./ResultsList";
import WatchedSummary from "./WatchedSummary";
import UserMoviesList from "./UserMoviesList";
import MovieDetails from "./MovieDetails";

export default function App() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [movieIdSelected, setMovieIdSelected] =
    useState(null);
  const [isExistMovie, setIsExistMovie] = useState(false);

  const getMovies = async () => {
    try {
      setIsLoading(true);
      setError("");
      const response = await fetch(
        `http://www.omdbapi.com/?apikey=a862ad48&s=${query}`
      );

      if (!response.ok)
        throw new Error(
          "Something went wrong with fetching movies"
        );

      const data = await response.json();

      if (data.Response === "False")
        throw new Error("Movie not found!");

      setMovies(data.Search);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const selectMovieHandler = (id) => {
    setMovieIdSelected((mid) => (mid === id ? null : id));
  };

  const closeMovieHandler = () => {
    setMovieIdSelected(null);
    setIsExistMovie(
      watched
        .map((movie) => movie.imdbID)
        .includes(movieIdSelected)
    );
  };

  const addWatchedHandler = (movieWatched) => {
    if (isExistMovie === false)
      setWatched(
        (watched) => (watched = [...watched, movieWatched])
      );

    closeMovieHandler();
  };

  useEffect(() => {
    if (query.length < 3) {
      setMovies([]);
      setError("");
      return;
    }

    getMovies();
  }, [query]);

  return (
    <>
      <NavBar>
        <SearchBox query={query} setQuery={setQuery} />
        <ResultsNumber movies={movies} />
      </NavBar>
      <main className='main'>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && (
            <ResultsList
              movies={movies}
              onSelectMovie={selectMovieHandler}
            />
          )}
          {error && <ErrorMessage errorMessage={error} />}
        </Box>
        <Box>
          {movieIdSelected ? (
            <MovieDetails
              movieIdSelected={movieIdSelected}
              onCloseMovie={closeMovieHandler}
              onAddwatched={addWatchedHandler}
              isExistMovie={isExistMovie}
            />
          ) : (
            <>
              <WatchedSummary watched={watched} />
              <UserMoviesList watched={watched} />
            </>
          )}
        </Box>
      </main>
    </>
  );
}
