import { useEffect, useState } from "react";
import NavBar from "./NavBar";
import SearchBox from "./SerachBox";
import ResultsNumber from "./ResultsNumber";
import Box from "./Box";
import Loader from "./Loader";
import ErrorMessage from "./ErrorMessage";
import ResultsList from "./ResultsList";
import WatchedSummary from "./WatchedSummary";
import UserMoviesList from "./UserMoviesList";
import StarRating from "./StarRating";

const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const tempMovieData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
  },
  {
    imdbID: "tt0133093",
    Title: "The Matrix",
    Year: "1999",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg",
  },
  {
    imdbID: "tt6751668",
    Title: "Parasite",
    Year: "2019",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg",
  },
];

const apiKey = "a862ad48";

export default function App() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState(tempWatchedData);
  const [movieRating, setMovieRating] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const queryTemp = "interstellar";

  console.log(query);

  const getMovies = async () => {
    try {
      setIsLoading(true);
      setError("");
      const response = await fetch(
        `http://www.omdbapi.com/?apikey=${apiKey}&s=${query}`
      );

      if (!response.ok)
        throw new Error(
          "Something went wrong with fetching movies"
        );

      const data = await response.json();

      if (data.Response === "False")
        throw new Error("Movie not found!");

      setMovies(data.Search);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (query.length < 3) {
      setMovies([]);
      setError("");
      return;
    }
    getMovies();
  }, []);

  return (
    <>
      <NavBar>
        <SearchBox query={query} setQuery={setQuery} />
        <ResultsNumber movies={movies} />
      </NavBar>
      <main className='main'>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && (
            <ResultsList movies={movies} />
          )}
          {error && <ErrorMessage errorMessage={error} />}
        </Box>
        <Box>
          <WatchedSummary watched={watched} />
          <UserMoviesList watched={watched} />
          <StarRating onSetRating={setMovieRating} />
        </Box>
      </main>
    </>
  );
}
