import { useEffect, useState } from "react";
import NavBar from "./NavBar";
import SearchBox from "./SerachBox";
import ResultsNumber from "./ResultsNumber";
import Box from "./Box";
import ResultsList from "./ResultsList";
import WatchedSummary from "./WatchedSummary";
import UserMoviesList from "./UserMoviesList";
import StarRating from "./StarRating";

const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const tempMovieData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
  },
  {
    imdbID: "tt0133093",
    Title: "The Matrix",
    Year: "1999",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg",
  },
  {
    imdbID: "tt6751668",
    Title: "Parasite",
    Year: "2019",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg",
  },
];

const apiKey = "a862ad48";

export default function App() {
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState(tempWatchedData);
  const [movieRating, setMovieRating] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const query = "interstellar";

  const getMovies = async () => {
    setIsLoading(true);
    const response = await fetch(
      `http://www.omdbapi.com/?apikey=${apiKey}&s=${query}`
    );
    const data = await response.json();
    setMovies(data.Search);
    setIsLoading(false);
  };

  useEffect(() => {
    getMovies();
  }, []);

  return (
    <>
      <NavBar>
        <SearchBox />
        <ResultsNumber movies={movies} />
      </NavBar>
      <main className='main'>
        <Box>
          {isLoading ? (
            <Loader />
          ) : (
            <ResultsList movies={movies} />
          )}
        </Box>
        <Box>
          <WatchedSummary watched={watched} />
          <UserMoviesList watched={watched} />
          <StarRating onSetRating={setMovieRating} />
        </Box>
      </main>
    </>
  );
}
