const UserMoviesList = ({ watched, onRemoveWatched }) => {
  return (
    <ul className='list'>
      {watched.map((movie) => (
        <li key={movie.imdbID}>
          <img
            src={movie.poster}
            alt={`${movie.title} poster`}
          />
          <h3>{movie.title}</h3>
          <div>
            <p>
              <span>‚≠êÔ∏è</span>
              <span>{movie.imdbRating}</span>
            </p>
            <p>
              <span>üåü</span>
              <span>{movie.userRating}</span>
            </p>
            <p>
              <span>‚è≥</span>
              <span>{movie.runtime} min</span>
            </p>
            <button
              onClick={() => onRemoveWatched(movie.imdbID)}
              className='btn-delete'
            >
              X
            </button>
          </div>
        </li>
      ))}
    </ul>
  );
};

export default UserMoviesList;
